import React, { useState } from 'react';
//import axios from 'axios';
import './App.css';

const API_KEY = 'your_api_key'; // Replace 'your_api_key' with your OpenWeatherMap API key

const Weather = ({ data }) => {
  return (
    <div className="weather">
      <h2>Current Weather</h2>
      <p>Location: {data.name}, {data.sys.country}</p>
      <p>Temperature: {data.main.temp} Â°C</p>
      <p>Humidity: {data.main.humidity}%</p>
      <p>Wind Speed: {data.wind.speed} m/s</p>
    </div>
  );
};

const LocationInput = ({ onSubmit }) => {
  const [location, setLocation] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(location);
    setLocation('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Enter location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        required
      />
      <button type="submit">Get Weather</button>
    </form>
  );
};

const App = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const fetchWeather = async (location) => {
    try {
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}&units=metric`);
      setWeatherData(response.data);
      setError(null);
    } catch (error) {
      setWeatherData(null);
      setError('Location not found. Please try again.');
    }
  };

  return (
    <div className="App">
      <h1>Simple Weather App</h1>
      <LocationInput onSubmit={fetchWeather} />
      {error && <p className="error">{error}</p>}
      {weatherData && <Weather data={weatherData} />}
    </div>
  );
};

export default App;
